function GetLatestReleaseInfo(){$.getJSON("https://api.github.com/repos/sifex/Scarlet/releases/latest").done(function(e){for(var a=e.assets[0],t=0,o=0;o<e.assets.length;o++)t+=e.assets[o].download_count;var s=36e5,n=24*s,d=new Date-new Date(a.updated_at),r;r=d<n?(d/s).toFixed(1)+" hours ago":(d/n).toFixed(1)+" days ago";var l=e.name+" was updated "+r+" and downloaded "+t.toLocaleString()+" times.";$(".download a").attr("href",a.browser_download_url),$(".release-info").text(l),$(".release-info").fadeIn("slow")})}$(document).ready(function(){GetLatestReleaseInfo()});